mapper.insert:
   	INSERT INTO MEMBER
		(
			USER_NO
			,USER_ID
			,USER_PWD
			,USER_NAME
			,NICKNAME
			,PROFILE_PATH
			,EMAIL
			,USER_PHONE
			,STATUS
			,CREATE_DATE
			,MODIFY_DATE
		)
		VALUES
		(
			SEQ_USER_NO.NEXTVAL
			, #{ id }
			, #{ pwd }
			, #{ userName }
			, #{ nickName }

			, #{ profilePath }


			, null

			, #{ email }

			, #{ phone }


			, null

			, DEFAULT
			, SYSDATE
			, SYSDATE
		)

mapper.select:
    SELECT USER_ID
	FROM MEMBER
	WHERE USER_NAME = #{userName}
	AND USER_PHONE = #{phone}

   	SELECT USER_ID,USER_PWD,EMAIL
	FROM MEMBER
	WHERE USER_ID = #{id}
	AND EMAIL = #{email}

mapper.update:
UPDATE MEMBER
SET USER_PWD = #{pwd}
WHERE USER_ID = #{id}
