mapper.select:
SELECT * FROM (SELECT ROWNUM RNUM, A.* FROM (SELECT C.CAMP_NO AS "CAMP_NO" , C.CAMP_NAME AS "CAMP_NAME" , SUBSTR(CAMP_ADDRESS, 0, INSTR(CAMP_ADDRESS, ' ', 1, 3)) AS "CAMP_ADDRESS" , F.FILE_ROUTE AS "FILE_ROUTE" , F.FILE_NAME AS "FILE_NAME" , SUM(R.ROOM_COUNT) AS "ROOM_COUNT" , MAX(R.ROOM_MEMBER) AS "MAX_GUEST" , MIN(R.ROOM_PRICE) AS "MIN_PRICE" , MAX(R.ROOM_PRICE) AS "MAX_PRICE" FROM (SELECT CAMP_NO , CAMP_NAME , CAMP_ADDRESS FROM CAMP WHERE CAMP_ADDRESS LIKE '%'||#{ fc.sArea }||'%' AND CAMP_STATUS = 'Y') C JOIN ROOM R ON R.CAMP_NO = C.CAMP_NO JOIN CAMP_FILE I ON C.CAMP_NO = I.CAMP_NO JOIN IMAGEFILE F ON I.FILE_NO = F.FILE_NO WHERE F.FILE_STATUS = 'N' AND F.FILE_LEVEL = 1 AND R.ROOM_MEMBER >= #{ fc.sGuest }
AND P.BUSINESS_TYPE IN
#{type}
GROUP BY C.CAMP_NO, C.CAMP_NAME, C.CAMP_ADDRESS, F.FILE_ROUTE, F.FILE_NAME, R.ROOM_MEMBER ORDER BY C.CAMP_NAME ASC )A) WHERE ROWNUM BETWEEN #{ startRow } AND #{ endRow }

SELECT COUNT(CAMP_NO) FROM (SELECT C.CAMP_NO , C.CAMP_NAME , C.CAMP_ADDRESS , F.FILE_ROUTE , F.FILE_NAME , SUM(R.ROOM_COUNT) AS "ROOM_COUNT" , MAX(R.ROOM_MEMBER) AS "MAX_GUEST" , MIN(R.ROOM_PRICE) AS "MIN_PRICE" , MAX(R.ROOM_PRICE) AS "MAX_PRICE" FROM (SELECT CAMP_NO , CAMP_NAME , CAMP_ADDRESS FROM CAMP WHERE CAMP_ADDRESS LIKE '%'||#{ fc.sArea }||'%'
AND CAMP_NAME LIKE '%'||#{ fc.sName }||'%'
AND CAMP_STATUS = 'Y') C JOIN ROOM R ON R.CAMP_NO = C.CAMP_NO JOIN CAMP_FILE I ON C.CAMP_NO = I.CAMP_NO JOIN IMAGEFILE F ON I.FILE_NO = F.FILE_NO JOIN CAMP_BUSINESS_TYPE T ON C.CAMP_NO = T.CAMP_NO JOIN BUSINESS_TYPE P ON P.BUSINESS_NO = T.BUSINESS_NO JOIN CAMP_FACILITY Y ON C.CAMP_NO = Y.CAMP_NO JOIN FACILITY L ON L.FACILITY_NO = Y.FACILITY_NO WHERE F.FILE_STATUS = 'N' AND R.ROOM_MEMBER >= #{ fc.sGuest }
AND P.BUSINESS_TYPE IN
#{type}
AND L.FACILITY_NAME = #{faci}
AND R.ROOM_FLOOR IN
#{floor}
GROUP BY C.CAMP_NO, C.CAMP_NAME, C.CAMP_ADDRESS, F.FILE_ROUTE, F.FILE_NAME, R.ROOM_MEMBER )

SELECT * FROM (SELECT ROWNUM RNUM, A.* FROM (SELECT C.CAMP_NO AS "CAMP_NO" , C.CAMP_NAME AS "CAMP_NAME" , SUBSTR(CAMP_ADDRESS, 0, INSTR(CAMP_ADDRESS, ' ', 1, 3)) AS "CAMP_ADDRESS" , F.FILE_ROUTE AS "FILE_ROUTE" , F.FILE_NAME AS "FILE_NAME" , SUM(R.ROOM_COUNT) AS "ROOM_COUNT" , MAX(R.ROOM_MEMBER) AS "MAX_GUEST" , MIN(R.ROOM_PRICE) AS "MIN_PRICE" , MAX(R.ROOM_PRICE) AS "MAX_PRICE" FROM (SELECT CAMP_NO , CAMP_NAME , CAMP_ADDRESS FROM CAMP WHERE CAMP_ADDRESS LIKE '%'||#{ fc.sArea }||'%'
AND CAMP_NAME LIKE '%'||#{ fc.sName }||'%'
AND CAMP_STATUS = 'Y') C JOIN ROOM R ON R.CAMP_NO = C.CAMP_NO JOIN CAMP_FILE I ON C.CAMP_NO = I.CAMP_NO JOIN IMAGEFILE F ON I.FILE_NO = F.FILE_NO JOIN CAMP_BUSINESS_TYPE T ON C.CAMP_NO = T.CAMP_NO JOIN BUSINESS_TYPE P ON P.BUSINESS_NO = T.BUSINESS_NO JOIN CAMP_FACILITY Y ON C.CAMP_NO = Y.CAMP_NO JOIN FACILITY L ON L.FACILITY_NO = Y.FACILITY_NO WHERE F.FILE_STATUS = 'N' AND F.FILE_LEVEL = 1 AND R.ROOM_MEMBER >= #{ fc.sGuest }
AND P.BUSINESS_TYPE IN
#{type}
AND L.FACILITY_NAME = #{faci}
AND R.ROOM_FLOOR IN
#{floor}
GROUP BY C.CAMP_NO, C.CAMP_NAME, C.CAMP_ADDRESS, F.FILE_ROUTE, F.FILE_NAME, R.ROOM_MEMBER ORDER BY C.CAMP_NAME ASC )A) WHERE RNUM BETWEEN #{ startRow } AND #{ endRow }
